		var bH = $("body").outerHeight(true);
		var mB = parseInt($("body").css("margin-bottom"));
		
		if (options == 'relative') {
			if (bH > getWindowHeight()) {
				$("footer#ftrSite").css("position","absolute");
				$("footer#ftrSite").css("width",wW + "px");
				$("footer#ftrSite").css("top",bH - fH + "px");
				$("body").css("overflow-x","hidden");
			} else {
				$("footer#ftrSite").css("position","fixed");
				$("footer#ftrSite").css("width",wW + "px");
				$("footer#ftrSite").css("top",wH - fH + "px");
			}
		} else { // Pinned option
			// Set CSS attributes for positioning footer
			$("footer#ftrSite").css("position","fixed");
			$("footer#ftrSite").css("width",wW + "px");
			$("footer#ftrSite").css("top",wH - fH + "px");
			$("body").css("height",(bH + mB) + "px");
		}
